mapper.select:
/*ojc.board.repository.BoardMapper.getWeekJobList*/ SELECT A.WEEK_ID , A.SUBJECT , A.DEPT_CODE , A.CREATE_ID , A.CREATE_DATE , A.UPDATE_DATE , A.DELETE_YN , B.WEEK_RP_JOB_CT FROM WEEK_JOB_REPORT A INNER JOIN WEEK_JOB_DETAIL_REPORT B ON A.WEEK_ID = B.WEEK_ID WHERE 1=1
AND A.SUBJECT LIKE '%' || #{subject} || '%'
AND A.DEPT_CODE = #{deptCode}
AND A.DEPT_NM LIKE '%' || #{deptNm} || '%'
AND A.CREATE_ID = #{createId}
AND WEEK_RP_JOB_CT LIKE '%' || #{weekRpJobCt} || '%'
AND DELETE_YN = 'Y'
