mapper.select:
/*ojc.board.repository.BoardMapper.getWeekJobList*/ SELECT A.WEEK_ID , A.SUBJECT , A.DEPT_CODE , A.CREATE_ID , TO_CHAR(TO_DATE(A.CREATE_DATE), 'YYYY-MM-DD') AS CREATE_DATE , A.UPDATE_DATE , A.DELETE_YN , B.WEEK_RP_JOB_CT , B.NX_WEEK_RP_JOB_CT , B.WEEK_PL_JOB_CT , B.NX_WEEK_PL_JOB_CT , B.ETC_CT FROM WEEK_JOB_REPORT A INNER JOIN WEEK_JOB_DETAIL_REPORT B ON A.WEEK_ID = B.WEEK_ID WHERE 1=1
AND A.WEEK_ID = #{weekId}
AND A.SUBJECT LIKE '%' || #{subject} || '%'
AND A.DEPT_CODE = #{deptCode}
AND A.DEPT_NM LIKE '%' || #{deptNm} || '%'
AND A.CREATE_ID = #{createId}
AND WEEK_RP_JOB_CT LIKE '%' || #{weekRpJobCt} || '%'
AND DELETE_YN = 'Y'
AND DELETE_YN = 'N'

mapper.update:
/*ojc.board.repository.BoardMapper.setReportUpD*/
 UPDATE WEEK_JOB_DETAIL_REPORT
	SET WEEK_RP_JOB_CT = #{weekRpJobCt}
	   ,NX_WEEK_RP_JOB_CT = #{nxWeekRpJobCt}
	   ,WEEK_PL_JOB_CT = #{weekPlJobCt}
	   ,NX_WEEK_PL_JOB_CT = #{nxWeekPlJobCt}
	   ,UPDATE_DATE = SYSDATE
	   ,ETC_CT = #{etcCt}
	WHERE
	    WEEK_ID = #{weekId}

/*ojc.board.repository.BoardMapper.setReportUpD*/
 UPDATE WEEK_JOB_REPORT
	SET DELETE_YN = 'Y'
	WHERE
	    WEEK_ID = #{weekId}
